export default `:root{--color-primary: #2C3E50;--color-secondary: #1ABC9C;--color-background: #F4F6F7;--color-text: #2D3436;--color-success: #27AE60;--color-error: #E74C3C;--color-link: #2980B9;--color-hover: #D6DBDF;--color-primary-light: #34495E;--color-secondary-light: #2ECC71;--color-text-light: #7F8C8D;--shadow-sm: 0 1px 2px rgba(0, 0, 0, .05);--shadow-md: 0 4px 6px rgba(0, 0, 0, .1);--shadow-lg: 0 10px 15px rgba(0, 0, 0, .1);--spacing-xs: .25rem;--spacing-sm: .5rem;--spacing-md: 1rem;--spacing-lg: 1.5rem;--spacing-xl: 2rem;--border-radius-sm: .25rem;--border-radius-md: .5rem;--border-radius-lg: .75rem;--transition-fast: .15s ease-in-out;--transition-normal: .25s ease-in-out}*{margin:0;padding:0;box-sizing:border-box}body{font-family:Roboto,Segoe UI,Arial,sans-serif;background-color:var(--color-background);color:var(--color-text);line-height:1.6;font-size:16px}h1,h2,h3,h4,h5,h6{color:var(--color-primary);margin-bottom:var(--spacing-md);font-weight:500}h1{font-size:2rem}h2{font-size:1.75rem}h3{font-size:1.5rem}h4{font-size:1.25rem}h5{font-size:1.1rem}h6{font-size:1rem}p{margin-bottom:var(--spacing-md)}a{color:var(--color-link);text-decoration:none;transition:color var(--transition-fast)}a:hover{color:var(--color-primary)}.btn{display:inline-block;padding:var(--spacing-sm) var(--spacing-lg);border-radius:var(--border-radius-md);font-weight:500;text-align:center;cursor:pointer;transition:all var(--transition-normal);border:none;outline:none}.btn-primary{background-color:var(--color-secondary);color:#fff}.btn-primary:hover{background-color:var(--color-secondary-light)}.btn-secondary{background-color:var(--color-primary);color:#fff}.btn-secondary:hover{background-color:var(--color-primary-light)}.btn-outline{background-color:transparent;border:1px solid var(--color-secondary);color:var(--color-secondary)}.btn-outline:hover{background-color:var(--color-secondary);color:#fff}.form-group{margin-bottom:var(--spacing-md)}.form-label{display:block;margin-bottom:var(--spacing-xs);font-weight:500}.form-control{width:100%;padding:var(--spacing-sm) var(--spacing-md);border:1px solid var(--color-hover);border-radius:var(--border-radius-sm);background-color:#fff;transition:border-color var(--transition-fast)}.form-control:focus{outline:none;border-color:var(--color-secondary);box-shadow:var(--shadow-sm)}.alert{padding:var(--spacing-md);border-radius:var(--border-radius-md);margin-bottom:var(--spacing-md)}.alert-success{background-color:#27ae601a;border-left:4px solid var(--color-success);color:var(--color-success)}.alert-error{background-color:#e74c3c1a;border-left:4px solid var(--color-error);color:var(--color-error)}.container{width:100%;max-width:1200px;margin:0 auto;padding:0 var(--spacing-md)}.card{background-color:#fff;border-radius:var(--border-radius-md);box-shadow:var(--shadow-sm);padding:var(--spacing-lg);margin-bottom:var(--spacing-lg)}.header{background-color:var(--color-primary);color:#fff;padding:var(--spacing-md) 0;box-shadow:var(--shadow-md)}.navbar{display:flex;justify-content:space-between;align-items:center}.navbar-brand{font-size:1.5rem;font-weight:700;color:#fff}.navbar-nav{display:flex;list-style:none}.nav-item{margin-left:var(--spacing-md)}.nav-link{color:#fff;opacity:.8;transition:opacity var(--transition-fast)}.nav-link:hover,.nav-link.active{opacity:1}.sidebar{background-color:var(--color-primary);color:#fff;width:250px;height:100vh;position:fixed;top:0;left:0;padding:var(--spacing-lg) 0;transition:transform var(--transition-normal);z-index:1000}.sidebar-header{padding:0 var(--spacing-lg);margin-bottom:var(--spacing-lg)}.sidebar-menu{list-style:none}.sidebar-item{margin-bottom:var(--spacing-xs)}.sidebar-link{display:block;padding:var(--spacing-sm) var(--spacing-lg);color:#fffc;transition:all var(--transition-fast)}.sidebar-link:hover,.sidebar-link.active{background-color:#ffffff1a;color:#fff;border-left:4px solid var(--color-secondary)}.text-primary{color:var(--color-primary)}.text-secondary{color:var(--color-secondary)}.text-success{color:var(--color-success)}.text-error{color:var(--color-error)}.text-muted{color:var(--color-text-light)}.bg-primary{background-color:var(--color-primary)}.bg-secondary{background-color:var(--color-secondary)}.bg-success{background-color:var(--color-success)}.bg-error{background-color:var(--color-error)}.bg-light{background-color:var(--color-background)}.shadow-sm{box-shadow:var(--shadow-sm)}.shadow-md{box-shadow:var(--shadow-md)}.shadow-lg{box-shadow:var(--shadow-lg)}@media (max-width: 768px){.sidebar{transform:translate(-100%)}.sidebar.open{transform:translate(0)}.content-with-sidebar{margin-left:0}}@keyframes fadeIn{0%{opacity:0}to{opacity:1}}.fade-in{animation:fadeIn var(--transition-normal)}
`;